import {FC} from 'react';
import {FormControlLabel,Checkbox, Rating} from '@mui/material';
import {Speaker} from '@mui/icons-material';
import {useFormik} from 'formik';


const CustomizableSelector:FC<{isSelected:boolean}> = (props) => {
    const formik = useFormik({
        initialValues: {
           speakers:{
               checked: true,
                quantity: 2
           }
        },
        onSubmit: (values) => {
            console.log(values);
        }
    })

    const handleCheckboxChange = (event: React.ChangeEvent<HTMLInputElement>) => {
        formik.setFieldValue('test', event.target.checked);
        //submit
        formik.handleSubmit();
    }


    return (
        <div className={`${props.isSelected ? 'sm:animate-expand' : 'sm:animate-shrink'} sm:h-[350px] h-[300px]  bg-[#2E49A6] text-white  shadow-2xl border-2 border-[#2E49A6] sm:cursor-pointer max-sm:w-[80%] w-[300px] relative `}>
            <h1 className={'text-[30px] w-full text-center p-2'}>Customize</h1>
            <div className={'p-3 flex items-center'}>
                <FormControlLabel control={<Checkbox onChange={handleCheckboxChange}  sx={{
                    color: 'white',
                    '&.Mui-checked': {
                        color: 'white',
                    },
                }}/>} label={'Speakers'} value={formik.initialValues.speakers.checked} />
                <Rating name={'Speaker'} value={formik.initialValues.speakers.quantity} icon={<Speaker sx={{color:'white'}}/>} emptyIcon={<Speaker/>} />
            </div>
        </div>
    );
};

export default CustomizableSelector;
