import React from 'react';
import {ReactComponent as Speaker} from './SVG Files/Speaker + stand.svg';
import {ReactComponent as Booth} from './SVG Files/Booth.svg';
import {ReactComponent as SpeakerWithBass} from './SVG Files/Speaker + bass.svg';
import {ReactComponent as MovingHead} from './SVG Files/Moving head.svg';
import {ReactComponent as Gigbar} from './SVG Files/Gigbar with stand.svg';

import { FC } from 'react';

type allowedStrings =  "Booth" | 'SpeakerWithBass' | "MovingHead" | "Gigbar" | 'Speaker';
interface EquipmentType {
    component: allowedStrings;
    svgHeightToWidthRatio: number;
    width?: number;
    height?: number;
}

const Equipment: EquipmentType[] = [
    {
        component: "Speaker",
        svgHeightToWidthRatio: 3,
        width: 120,

    },
    {
        component: "Booth",
        svgHeightToWidthRatio: 1.23,
        width:340,
    },
    {
        component: "SpeakerWithBass",
        svgHeightToWidthRatio: 0.3,
        width: 140,
    },
    {
        component: "MovingHead",
        svgHeightToWidthRatio: 0.15,
        width: 80,

    }]




const EquipmentComponent:FC<{component: allowedStrings}> = (props) => {
    console.log(Speaker)
    //mapStringToComponent
    const mapStringToComponent = (component: string, width: number) => {

        switch (component) {
            case "Booth":
                return <Booth width={width} />;
            case "SpeakerWithBass":
                return <SpeakerWithBass width={width} />;
            case "MovingHead":
                return <MovingHead width={width} />;
            case "Gigbar":
                return <Gigbar width={width} />;
            case "Speaker":
                return <Speaker width={width} />;
            default:
                return <Booth width={width} />;
        }
    }

    // }

    return (
      mapStringToComponent(props.component, 100)
    )


    }

export default EquipmentComponent;
