import {FC, useEffect,useReducer} from 'react';
import {FormControlLabel,Checkbox, Rating} from '@mui/material';
import {Speaker,SpeakerGroup, Light, Fluorescent} from '@mui/icons-material';
import {calculateNewValue} from '@testing-library/user-event/dist/utils';

interface EquipmentState {
    checked: boolean;
    quantity: number;
}

type State = {
    [equipmentName: string]: EquipmentState;
};

// Action types
interface ToggleCheckAction {
    type: 'TOGGLE_CHECK';
    payload: {
        id: number;
    };
}

interface SetQuantityAction {
    type: 'SET_QUANTITY';
    payload: {
        id: number;
        quantity: number | null;
    };
}


type Action = ToggleCheckAction | SetQuantityAction;

const initialState = {
    0: {
        checked: true,
        quantity: 1,
    },
    1: {
        checked: false,
        quantity: 0,
    },
    2: {
        checked: false,
        quantity: 0,
    }
};

const reducer = (state: State, action: Action) => {
    switch (action.type) {
        case 'TOGGLE_CHECK':
            return {
                ...state,
                [action.payload.id]: {
                    ...state[action.payload.id],
                    checked: !state[action.payload.id].checked,
                },
            };
        case 'SET_QUANTITY':
            return {
                ...state,
                [action.payload.id]: {
                    ...state[action.payload.id],
                    quantity: action.payload.quantity ?? 0,
                },
            };
        default:
            return state;
    }
};

const CustomizableSelector:FC<{isSelected:boolean}> = (props) => {
    const [state, dispatch] = useReducer(reducer, initialState);

    const equipmentList = [{
            id: 0,
            name: 'Speaker',
            icon: <SpeakerGroup sx={{color:'white'}}/>,
            emptyIcon: <SpeakerGroup/>,
            min: 1,
            max:3,
    },
        {
            id: 1,
            name: 'Bass',
            icon: <SpeakerGroup sx={{color:'white'}}/>,
            emptyIcon: <SpeakerGroup/>,
            min: 0,
            max: state[0].quantity

        },
        {
            id: 2,
            name: 'Moving Head',
            icon: <Light sx={{color:'white'}}/>,
            emptyIcon: <Light/>,
            min: 0,
            max: 6


        },
        {
            id: 3,
            name: 'Gigbar',
            icon: <Fluorescent sx={{color:'white'}}/>,
            emptyIcon: <Fluorescent/>,

        }
    ];

    const handleCheckboxChange = (equipmentId: number) => (event: React.ChangeEvent<HTMLInputElement>) => {
        dispatch({
            type: 'TOGGLE_CHECK',
            payload: {
                id: equipmentId
            },
        });
    };

    const handleRatingChange = (equipmentId: number) => (event: any, newValue: number | null) => {
        let finalValue = newValue;

        if (equipmentId === 0 && (!newValue || newValue < 1)) {  // Assuming the Speaker has id 0
            finalValue = 1;
        }

        dispatch({
            type: 'SET_QUANTITY',
            payload: {
                id: equipmentId,
                quantity: finalValue,
            },
        });
    };


    return (
        <div className={`${props.isSelected ? 'sm:animate-expand' : 'sm:animate-shrink'} sm:h-[350px] h-[300px]  bg-[#2E49A6] text-white  shadow-2xl border-2 border-[#2E49A6] sm:cursor-pointer max-sm:w-[80%] w-[300px] relative `}>
            <h1 className={'text-[30px] w-full text-center p-2'}>Customize</h1>
            <div className={'p-3 flex-col'}>
                {equipmentList.map((equipment) => {
                    return (
                        <div key={equipment.id} className={'flex items-center'}>
                            <FormControlLabel  control={<Checkbox  checked={state[equipment.id]?.checked} onChange={equipment.id == 0 ? undefined :handleCheckboxChange(equipment.id)} sx={{
                                color: 'white',
                                '&.Mui-checked': {
                                    color: 'white',
                                },
                            }}/>} name={equipment.name} id={equipment.name} label={equipment.name} />
                            <div>
                                <Rating value={state[equipment.id].quantity} onChange={handleRatingChange(equipment.id)} name={equipment.name} max={equipment.max} icon={equipment.icon} emptyIcon={equipment.emptyIcon}   />
                            </div>
                        </div>
                    )
                }
                )}
            </div>
        </div>
    );
};

export default CustomizableSelector;
