import React from 'react';
import {Paper} from '@mui/material';
import {useForm} from '../../../Contexts/FormContext';
import {usePackage} from '../../../Contexts/PackageContext';
import {packs} from '../../../lib/packages';
import {eventTypes} from '../../../lib/eventTypes';

const FormSummary = () => {
    const {formik} = useForm();
    const {selectedPackage} = usePackage();
    const values = formik.values;
    const readableDate = formik.values.date && formik.values.date['$d'].toLocaleDateString()
    const readableTime = formik.values.date && formik.values.date['$d'].toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})
    const mapSelectedPackageToPack = (selectedPackage:number) => {
        switch(selectedPackage) {
            case 0:
                return packs[0];
            case 1:
                return packs[1];
            case 2:
                return packs[2];
            case 3:
                return packs[3];
            default:
                return packs[0];
        }
    }
    const mapSelectedTypeToType = (selectedType: number) => {
        const eventType = eventTypes.find((eventType) => eventType.id === selectedType);

        if (eventType) {
            return eventType.name;
        } else {
            return 'Other';
        }
    };
    return (
        <Paper className={'p-4 mt-3 max-h-[475px] overflow-y-scroll'} elevation={4}>
            <h1 className={'w-full text-center'}>Summary</h1>
            <div className={'space-y-4'}>
                    <ul className={'w-full'}>
                        <h2 className={'text-center'}>Personal Details</h2>
                        <li>Name: {values.name}</li>
                        <li>Email: {values.email}</li>
                        <li>Phone: {values.phone}</li>
                    </ul>
                    <ul className={'w-full'}>
                        <h2 className={'text-center'}>Event Details</h2>
                        <li>Date: {readableDate} at {readableTime}</li>
                        <li>Event type: {mapSelectedTypeToType(values.type)}</li>
                        <li>{values.location}</li>
                        <li>Number of guests: {values.guestRange[0]} - {values.guestRange[1]}</li>
                    </ul>
                    <ul>
                        <h2>Equipment</h2>
                        <li>Package : {mapSelectedPackageToPack(selectedPackage).title}</li>
                        <h3>Includes</h3>
                        <ul className={'space-y-2'}>
                            {mapSelectedPackageToPack(selectedPackage).items.map((item) => {
                                return (
                                    <li>{item.quantity}x {item.name}</li>
                                )
                            })}
                        </ul>
                        <h3>Additional Equipment</h3>
                        <ul className={'space-y-2'}>
                            {values.extraEquipment.map((item:any) => {
                                return (
                                    <>
                                        {item.quantity > 0 && <li>{item.quantity}x {item.name}</li>}
                                    </>
                                )
                            })}
                        </ul>
                    </ul>
            </div>

        </Paper>
    );
};

export default FormSummary;
