import {FC} from 'react';
import SwipeableViews from 'react-swipeable-views';
import PackageSelector from '../PackageSelector';

const styles = {
    slide: {
        padding: 15,
        minHeight: 100,
        color: '#fff',
        width: '100%'
    },
    slide1: {
        background: '#FEA900',
    },
    slide2: {
        background: '#B3DC4A',
    },
    slide3: {
        background: '#6AC0FF',
    },
};


const PackageTabNav:FC<{packages:any}> = (props) => {
    return (
        <div className={'w-full'}>
            <SwipeableViews>
                {/*<div style={Object.assign({}, styles.slide, styles.slide1)}>*/}
                {/*    slide n°1*/}
                {/*</div>*/}
                {/*<div style={Object.assign({}, styles.slide, styles.slide2)}>*/}
                {/*    slide n°2*/}
                {/*</div>*/}
                {/*<div style={Object.assign({}, styles.slide, styles.slide3)}>*/}
                {/*    slide n°3*/}
                {/*</div>*/}
                {props.packages.map((pack:any) => {
                    return (
                        <div style={Object.assign({}, styles.slide, styles.slide1)}>
                            <PackageSelector  onClick={pack.onClick} title={pack.title} items={pack.items} basePrice={pack.basePrice} isSelected={pack.isSelected}/>
                        </div>
                    )
                })}
            </SwipeableViews>
        </div>
    );
}

export default PackageTabNav;
