import  {FC} from 'react';
import Card from './Card';

interface item {
    name: string;
    price: number;
    quantity: number;
}

const PackageSelector:FC<{title:string, items?:item[], basePrice:number, onClick: any}> = (props) => {
    const equipmentPrice = props.items?.reduce((acc, item) => {
        return acc + item.price * item.quantity;
    }
    , 0);
    return (
       <Card>
           <div onClick={props.onClick} className="w-[280px] h-[280px]">
               <h1 className={'text-[40px] w-full text-center'}>{props.title}</h1>
               <ul className="list-disc ml-4 text-[20px] h-1/2 overflow-y-scroll">
                   {props.items?.map((item) => {
                          return (
                            <li>{item.quantity}x {item.name} </li>
                          )
                   })}
               </ul>
               <div className={'pt-8'}>
                   <h2 className={'w-full text-center text-2xl'}>Estimated Price: {equipmentPrice && equipmentPrice + props.basePrice}</h2>
                   <button className='bg-white text-black rounded-2xl w-full'>Get a quote</button>
               </div>
           </div>
       </Card>
    );
};

export default PackageSelector;
