import {FC, useState} from 'react';
import {Card, Checkbox} from '@mui/material';

const ExtraEquipmentCard:FC<{name: string, img: any}> = (props) => {
    const [selected, setSelected] = useState(false)
    const [quantity, setQuantity] = useState(0)
    const handleClick = () => {
        if(!selected)
            setQuantity(1)
        setSelected(!selected)

    }
    const handleQuantityChange = (e: any) => {
        setQuantity(e.target.value)
    }
    return (
        <>
            <Card  className={'relative w-fit flex flex-col justify-center items-center'}>
                <div className={'w-full flex'}>
                    <Checkbox onClick={handleClick} checked={selected}/>
                    <div className={'flex align-middle items-center'}>
                        <h3 className={'flex justify-end items-center pr-4 font-light'}>Quantity</h3>
                        <input min={1} onChange={handleQuantityChange} value={quantity} className={'w-[50px] h-fit outline-none'} type='number'/>
                    </div>
                </div>
                <img onClick={handleClick} width={100} height={100}  src={props.img}/>
                <h1 className={'font-bold'}>{props.name}</h1>
            </Card>

        </>


    );
};

export default ExtraEquipmentCard;
