import {FC, useState} from 'react';

interface item {
    name: string;
    price: number;
    quantity: number;
}

const PackageSelector:FC<{title:string, items?:item[], basePrice:number, onClick:(arg:string)=> void,isSelected:boolean}> = (props) => {
    const equipmentPrice = props.items?.reduce((acc, item) => {
        return acc + item.price * item.quantity;
    }
    , 0);
    const onClickHandler = () => {
        props.onClick(props.title);
    }
    return (
        <div onClick={onClickHandler} className={`${props.isSelected ? 'animate-expand' : 'animate-shrink'} bg-[#2E49A6] text-white  shadow-2xl border-2 border-[#2E49A6] hover:border-[#0FBB6F] cursor-pointer w-[300px] relative`}>
            <h1 className={'text-[30px] w-full text-center'}>{props.title}</h1>
            <div>
                <h2 className={'w-full text-center'}>Estimated Price:</h2>
                <h1 className={'w-full text-center text-4xl'}> ${equipmentPrice && equipmentPrice + props.basePrice}</h1>
            </div>
               <ul className="text-[20px] overflow-y-scroll text-center max-h-[40%]">
                   {props.items?.map((item) => {
                          return (
                            <li>{item.quantity}x {item.name} </li>
                          )
                   })}
                   <li>3X Speakers</li>
               </ul>
               <div className={'absolute bottom-0 bg-red-400 w-full p-3'}>
                   <button className='bg-white text-black rounded-2x w-full'>Get a quote</button>
               </div>
       </div>
    );
};

export default PackageSelector;
