import React from 'react';
import './App.css';
import EquipmentComponent from './Components/Equipment/EquipmentComponent';
import PackageSelector from './Components/PackageSelector';

const standardPack = [
    {
        name: 'Speaker',
        quantity: 2,
        price: 35
    },
    {
        name: 'Booth',
        quantity: 1,
        price: 30
    },
    {
        name: 'Floor Lights',
        quantity: 6,
        price: 10
    }
]
const premiumPack = [
    {
        name: 'Speaker',
        quantity: 2,
        price: 35
    },
    {
        name: 'Booth',
        quantity: 1,
        price: 30
    },
    {
        name: 'Floor Lights',
        quantity: 6,
        price: 10
    },
    {
        name: 'Microphone',
        quantity: 1,
        price: 40
    }
]
function App() {
  return (
      <div className={'background'}>
          <header className='w-full h-[80px] bg-black'>
              test
          </header>
          <div className="">
              <div className="w-full h-[500px] flex  items-center space-x-7 p-5">
                  <EquipmentComponent component='Speaker'/>
                  <EquipmentComponent component='MovingHead'/>
                  <EquipmentComponent component='SpeakerWithBass'/>
                  <EquipmentComponent component='Booth'/>
                  <EquipmentComponent component='SpeakerWithBass'/>
                  <EquipmentComponent component='MovingHead'/>
                  <EquipmentComponent component='Speaker'/>
              </div>
              <div className="flex ml-8 h-fit space-x-5">
                  <PackageSelector title={'Basic'} items={standardPack} basePrice={500}/>
                    <PackageSelector title={'Premium'} items={premiumPack} basePrice={600}/>
                    {/*<PackageSelector title={'Deluxe'}/>*/}
              </div>
          </div>

      </div>

  );
}

export default App;
