import React, {FC, useState} from 'react';
import PackageSelector from '../PackageSelector';
import {equipmentList} from '../../lib/equipment';
const {speaker, bass, booth, movingHead} = equipmentList;
const basicPack = [
    {
        name: speaker.name,
        price: speaker.price,
        quantity: 2
    },
    {
        name: booth.name,
        price: booth.price,
        quantity: 1
    }
];
const standardPack = [
    {
        name: speaker.name,
        price: speaker.price,
        quantity: 2
    },
    {
        name: booth.name,
        price: booth.price,
        quantity: 1
    },
    {
        name: bass.name,
        price: bass.price,
        quantity: 2
    }
];

const deluxePack = [
    {
        name: speaker.name,
        price: speaker.price,
        quantity: 2,
    },
    {
        name: booth.name,
        price: booth.price,
        quantity: 1
    },
    {
        name: bass.name,
        price: bass.price,
        quantity: 2
    },
    {
        name: movingHead.name,
        price: movingHead.price,
        quantity: 2
    },
    ]
const PackageList:FC<{onPackageSelect: (arg:string)=>void}> = (props) => {
    return (
        <div className="flex p-8 space-x-0.5 h-fit overflow-x-scroll">
            <PackageSelector onClick={props.onPackageSelect}  title={'Basic'} items={basicPack} basePrice={500}/>
            <PackageSelector onClick={props.onPackageSelect}  title={'Standard'} items={standardPack} basePrice={600}/>
            <PackageSelector onClick={props.onPackageSelect}   title={'Deluxe'} basePrice={800} items={deluxePack}/>
        </div>
    );
};

export default PackageList;
