import {FC, useEffect,useReducer} from 'react';
import {FormControlLabel,Checkbox, Rating} from '@mui/material';
import {Speaker,SpeakerGroup, Light} from '@mui/icons-material';
import {calculateNewValue} from '@testing-library/user-event/dist/utils';

interface EquipmentState {
    checked: boolean;
    quantity: number;
}

type State = {
    [equipmentName: string]: EquipmentState;
};

// Action types
interface ToggleCheckAction {
    type: 'TOGGLE_CHECK';
    payload: {
        name: string;
    };
}

interface SetQuantityAction {
    type: 'SET_QUANTITY';
    payload: {
        name: string;
        quantity: number | null;
    };
}

type Action = ToggleCheckAction | SetQuantityAction;

const initialState = {
    Speaker: {
        checked: true,
        quantity: 1,
    },
    Bass: {
        checked: false,
        quantity: 0,
    },
    MovingHead: {
        checked: false,
        quantity: 0,
    }
};

const reducer = (state: State, action: Action) => {
    switch (action.type) {
        case 'TOGGLE_CHECK':
            return {
                ...state,
                [action.payload.name]: {
                    ...state[action.payload.name],
                    checked: !state[action.payload.name].checked,
                },
            };
        case 'SET_QUANTITY':
            return {
                ...state,
                [action.payload.name]: {
                    ...state[action.payload.name],
                    quantity: action.payload.quantity ?? 0,
                },
            };
        default:
            return state;
    }
};

const CustomizableSelector:FC<{isSelected:boolean}> = (props) => {
    const [state, dispatch] = useReducer(reducer, initialState);

    const equipmentList = [{
        id: 0,
        name: 'Speaker',
        icon: <SpeakerGroup sx={{color:'white'}}/>,
        emptyIcon: <SpeakerGroup/>,
        min: 1,
        max:3,
    },
        {
            id: 1,
            name: 'Bass',
            icon: <SpeakerGroup sx={{color:'white'}}/>,
            emptyIcon: <SpeakerGroup/>,
            min: 0,
            max: state.Speaker.quantity

        },
        {
            id: 2,
            name: 'MovingHead',
            icon: <Light sx={{color:'white'}}/>,
            emptyIcon: <Light/>,
            min: 0,
            max: 6


        }
    ];

    const handleCheckboxChange = (equipmentName: string) => (event: React.ChangeEvent<HTMLInputElement>) => {
        dispatch({
            type: 'TOGGLE_CHECK',
            payload: {
                name: equipmentName
            },
        });
    };

    const handleRatingChange = (equipmentName: string) => (event: any, newValue: number | null) => {
        let finalValue = newValue;

        // Ensure speaker always has a minimum value of 1
        if (equipmentName === "Speaker" && (!newValue || newValue < 1)) {
            finalValue = 1;
        }

        dispatch({
            type: 'SET_QUANTITY',
            payload: {
                name: equipmentName,
                quantity: finalValue,
            },
        });
    };

    return (
        <div className={`${props.isSelected ? 'sm:animate-expand' : 'sm:animate-shrink'} sm:h-[350px] h-[300px]  bg-[#2E49A6] text-white  shadow-2xl border-2 border-[#2E49A6] sm:cursor-pointer max-sm:w-[80%] w-[300px] relative `}>
            <h1 className={'text-[30px] w-full text-center p-2'}>Customize</h1>
            <div className={'p-3 flex-col'}>
                {equipmentList.map((equipment) => {
                    return (
                        <div key={equipment.id} className={'flex items-center'}>
                            <FormControlLabel  control={<Checkbox  checked={state[equipment.name]?.checked} onChange={equipment.name == 'Speaker' ? undefined :handleCheckboxChange(equipment.name)} sx={{
                                color: 'white',
                                '&.Mui-checked': {
                                    color: 'white',
                                },
                            }}/>} name={equipment.name} id={equipment.name} label={equipment.name} />
                            <div>
                                <Rating value={state[equipment.name].quantity} onChange={handleRatingChange(equipment.name)} name={equipment.name} max={equipment.max} icon={equipment.icon} emptyIcon={equipment.emptyIcon}   />
                            </div>
                        </div>
                    )
                }
                )}
            </div>
        </div>
    );
};

export default CustomizableSelector;
