import React from 'react';
import {Box, FormControl, InputLabel, MenuItem, Modal, Select, SwipeableDrawer, TextField} from '@mui/material';
import {usePackage} from '../../Contexts/PackageContext';
import {useFormik} from 'formik';
import QuoteForm from '../QuoteForm';

const QuoteModal = () => {
    const {isModalOpen, openModalHandler, selectedPackage} = usePackage();
    const onCloseHandler = () => {
        openModalHandler(false);
    }

    const formik = useFormik({
        initialValues: {
            name: '',
            email: '',
            phone: '',
            eventDate: '',
            eventLocation: '',
            eventTime: '',
            eventDuration: '',
            eventDetails: '',
            package: selectedPackage
        },
        onSubmit: (values) => {
            console.log(values);
        }
    })
    const style = {
        position: 'absolute' as 'absolute',
        top: '50%',
        left: '50%',
        transform: 'translate(-50%, -50%)',
        bgcolor: 'background.paper',
        border: '2px solid #000',
        boxShadow: 24,
        p: 4,
    };

    return (
        <>
            <Modal open={isModalOpen} onClose={onCloseHandler}>
               <QuoteForm/>
            </Modal>
            <SwipeableDrawer open={isModalOpen} onClose={null}>
                <QuoteForm/>
            </SwipeableDrawer>
        </>

    );
};

export default QuoteModal;
