import {FC, useEffect} from 'react';
import {FormControlLabel,Checkbox, Rating} from '@mui/material';
import {Speaker,SpeakerGroup} from '@mui/icons-material';
import {useFormik} from 'formik';


const equipmentList = [{
    name: 'Speaker',
    icon: <SpeakerGroup sx={{color:'white'}}/>,
},
    // {
    //     name: 'Bass',
    //     icon: <SpeakerGroup sx={{color:'white'}}/>,
    // },
];



const CustomizableSelector:FC<{isSelected:boolean}> = (props) => {
    const formik = useFormik({
        initialValues: {
            speaker: {
                checked: true,
                quantity: 2,
                icon: <Speaker sx={{color:'white'}}/>,
            },
            bass: {
                checked: false,
                quantity: 0,
                icon: <SpeakerGroup sx={{color:'white'}}/>,
            }
        },
        onSubmit: (values) => {
            console.log(values);
        }
    })

    useEffect(() => {
        console.log(formik.values)
    }, [formik.values]);

    const formikValues = {
        ...formik.initialValues,
        ...formik.values
    }


    return (
        <div className={`${props.isSelected ? 'sm:animate-expand' : 'sm:animate-shrink'} sm:h-[350px] h-[300px]  bg-[#2E49A6] text-white  shadow-2xl border-2 border-[#2E49A6] sm:cursor-pointer max-sm:w-[80%] w-[300px] relative `}>
            <h1 className={'text-[30px] w-full text-center p-2'}>Customize</h1>
            <div className={'p-3 flex-col items-center align-middle justify-center bg-red-400'}>
                {/*<FormControlLabel control={<Checkbox onChange={handleCheckboxChange}  sx={{*/}
                {/*    color: 'white',*/}
                {/*    '&.Mui-checked': {*/}
                {/*        color: 'white',*/}
                {/*    },*/}
                {/*}}/>} label={'Speakers'} checked={formik.initialValues.speakers.checked} />*/}
                {/*<Rating name={'Speaker'} value={formik.initialValues.speakers.quantity} max={6} icon={<Speaker sx={{color:'white'}}/>} emptyIcon={<Speaker/>} />*/}
                {equipmentList.map((equipment) => {
                    return (
                        <div className={'flex'}>
                            <FormControlLabel  control={<Checkbox onChange={formik.handleChange} sx={{
                                color: 'white',
                                '&.Mui-checked': {
                                    color: 'white',
                                },
                            }}/>} name={equipment.name} id={equipment.name} label={equipment.name} />
                            <div>
                                <Rating name={equipment.name} value={2} max={3} icon={equipment.icon} emptyIcon={equipment.icon}   />
                            </div>
                        </div>
                    )
                }
                )}
            </div>
        </div>
    );
};

export default CustomizableSelector;
