import React from 'react';
import {Box, FormControl, InputLabel, MenuItem, Modal, Select, SwipeableDrawer, TextField} from '@mui/material';
import {usePackage} from '../../Contexts/PackageContext';
import {useFormik} from 'formik';
import {ExpandMore} from '@mui/icons-material';
import QuoteForm from '../QuoteForm';

const QuoteModal = () => {
    const {isModalOpen, openModalHandler, selectedPackage} = usePackage();
    const onCloseHandler = () => {
        openModalHandler(false);
    }

    const formik = useFormik({
        initialValues: {
            name: '',
            email: '',
            phone: '',
            eventDate: '',
            eventLocation: '',
            eventTime: '',
            eventDuration: '',
            eventDetails: '',
            package: selectedPackage
        },
        onSubmit: (values) => {
            console.log(values);
        }
    })

    const toggleDrawer = (open: boolean) => (event: React.KeyboardEvent | React.MouseEvent) => {
        if (
          event &&
          event.type === 'keydown' &&
          ((event as React.KeyboardEvent).key === 'Tab' ||
            (event as React.KeyboardEvent).key === 'Shift')
        ) {
          return;
        }

        openModalHandler(open);
    }

    return (
        <>
            <div className={'max-sm:hidden'}>
                <Modal open={isModalOpen} onClose={onCloseHandler}>
                    <div className={'p-4 space-y-3 absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 bg-white'}>
                        <QuoteForm/>
                    </div>
                </Modal>
            </div>
            <div className={'md:hidden'}>
                <SwipeableDrawer sx={{height:'100%'}} anchor={'bottom'} open={isModalOpen} onOpen={toggleDrawer(true)} onClose={toggleDrawer(false)}>
                    <ExpandMore fontSize={'large'} sx={{color:'black', width:'100%'}}/>
                    <div className={'h-screen p-3 flex space-y-4'}>
                        <QuoteForm/>
                    </div>
                </SwipeableDrawer>
            </div>

        </>

    );
};

export default QuoteModal;
