import {FC, ReactNode} from 'react';

interface Props {
    children : ReactNode;
    isSelected:boolean;
    onClick:()=>void;
    isCustomize?:boolean;
}
const Card:FC<Props> = ({children, isSelected,onClick, isCustomize}) => {
    return (
        <div onClick={onClick}  className={`${ isSelected && !isCustomize ? 'sm:animate-expand' : !isCustomize && !isSelected ? 'sm:animate-shrink' : 'hover:opacity-[0.8]'} rounded-2xl  bg-blue-light text-white  shadow-2xl sm:cursor-pointer max-sm:w-[80%] w-[300px] relative shadow-custom-1 shadow-custom-2  `}>
            {children}
        </div>
    );
};

export default Card;
