import React from 'react';
import {FormControl, InputLabel, MenuItem, Select, Slider, TextField, Tooltip, Typography} from '@mui/material';
import {LocalizationProvider} from '@mui/x-date-pickers/LocalizationProvider';
import {AdapterDayjs} from '@mui/x-date-pickers/AdapterDayjs';
import {DatePicker} from '@mui/x-date-pickers/DatePicker';
import {useFormik} from 'formik';

const QuoteForm = () => {

    const formik = useFormik({
        initialValues: {
            name: '',
            email: '',
            phone: '',
            date: '',
            type: '',
            location: '',
            guestRange: [250,500]

        },
        onSubmit: values => {
            console.log(values);
        }
    })

    const valueLabelComponent = (props: any) => {
        const { children, open, value } = props;

        return (
            <Tooltip open={open} enterTouchDelay={0} placement="bottom" title={value}>
                {children}
            </Tooltip>
        );
    }

    return (
        <div className={'space-y-6 w-full px-4'}>
            <div className={''}>
                <h1 className={'text-center text-2xl font-bold'}>Get a quote</h1>
                <h2 className={'text-center'}>Fill out the following information</h2>
            </div>
            <div className={'w-full flex sm:space-x-3 max-sm:flex-col max-sm:space-y-4'}>
                <TextField sx={{width:'100%'}}  label={'Name'} variant='standard'/>
                <TextField sx={{width:'100%'}}  label={'Email'} variant='standard'/>
                <TextField sx={{width:'100%'}}  label={'Phone'} variant='standard'/>
            </div>
            <div className={'flex w-full space-x-3'}>
                <LocalizationProvider dateAdapter={AdapterDayjs}>
                    <DatePicker sx={{width:'100%'}} label="Date of event" />
                </LocalizationProvider>
                <FormControl fullWidth>
                    <InputLabel>Type of event</InputLabel>
                    <Select
                        label="Type of event"
                    >
                        <MenuItem value={1}>Wedding</MenuItem>
                        <MenuItem value={2}>Birthday</MenuItem>
                        <MenuItem value={3}>Engagement/Henna party</MenuItem>
                        <MenuItem value={3}>Bar/Bat Mitzvah</MenuItem>
                        <MenuItem value={4}>Corporate event</MenuItem>
                        <MenuItem value={5}>House party</MenuItem>
                        <MenuItem value={6}>Other</MenuItem>
                    </Select>
                </FormControl>
            </div>
            <TextField sx={{width:'100%'}}  label={'Location of event'} variant='standard'/>
            <div className={'flex space-x-5'}>
               <h4 className={'whitespace-nowrap text-center flex items-center'}>Number of guests</h4>
                <Slider
                    sx={{width:'w-full'}}
                    min={50}
                    step={5}
                    max={1000}
                    disableSwap
                    aria-valuetext={'guests'}
                    valueLabelDisplay={'auto'}
                    valueLabelComponent={valueLabelComponent}
                    valueLabelFormat={(value) => `${value} guests`}
                    value={formik.values.guestRange}
                    onChange={(event, value) => formik.setFieldValue('guestRange', value)}
                />
            </div>



        </div>
    );
};

export default QuoteForm;
