import React from 'react';
import {FormControl, InputLabel, MenuItem, Select, TextField} from '@mui/material';
import {LocalizationProvider} from '@mui/x-date-pickers/LocalizationProvider';
import {AdapterDayjs} from '@mui/x-date-pickers/AdapterDayjs';
import {DatePicker} from '@mui/x-date-pickers/DatePicker';
import {useFormik} from 'formik';

const QuoteForm = () => {

    const formik = useFormik({
        initialValues: {
            name: '',
            email: '',
            phone: '',
            date: '',
            type: '',
            location: ''
        },
        onSubmit: values => {
            console.log(values);
        }
    })

    return (
        <div className={'space-y-3 flex flex-col justify-center items-center align-middle'}>
            <div className={'bg-blue-light'}>
                <h1 className={'text-center text-2xl font-bold'}>Get a quote</h1>
                <h2 className={'text-center'}>Fill out the following information</h2>
            </div>
            <div className={'w-full flex sm:space-x-3 max-sm:flex-col space-y-3'}>
                <TextField sx={{width:'100%'}}  label={'Name'} variant='standard'/>
                <TextField sx={{width:'100%'}}  label={'Email'} variant='standard'/>
                <TextField sx={{width:'100%'}}  label={'Phone'} variant='standard'/>
            </div>
            <div className={'flex w-full space-x-3'}>
                <LocalizationProvider dateAdapter={AdapterDayjs}>
                    <DatePicker sx={{width:'100%'}} label="Date of event" />
                </LocalizationProvider>
                <FormControl fullWidth>
                    <InputLabel>Type of event</InputLabel>
                    <Select
                        label="Type of event"
                    >
                        <MenuItem value={1}>Wedding</MenuItem>
                        <MenuItem value={2}>Birthday</MenuItem>
                        <MenuItem value={3}>Engagement/Henna party</MenuItem>
                        <MenuItem value={3}>Bar/Bat Mitzvah</MenuItem>
                        <MenuItem value={4}>Corporate event</MenuItem>
                        <MenuItem value={5}>House party</MenuItem>
                        <MenuItem value={6}>Other</MenuItem>
                    </Select>
                </FormControl>
            </div>
            <TextField sx={{width:'100%'}}  label={'Location of event'} variant='standard'/>
        </div>
    );
};

export default QuoteForm;
