import React from 'react';
import {FormControl, InputLabel, MenuItem, Modal, Select, TextField} from '@mui/material';
import {usePackage} from '../../Contexts/PackageContext';
import {useFormik} from 'formik';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import {AdapterDayjs} from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';

const QuoteModal = () => {
    const {isModalOpen, openModalHandler, selectedPackage} = usePackage();
    const onCloseHandler = () => {
        openModalHandler(false);
    }

    const formik = useFormik({
        initialValues: {
            name: '',
            email: '',
            phone: '',
            eventDate: '',
            eventLocation: '',
            eventTime: '',
            eventDuration: '',
            eventDetails: '',
            package: selectedPackage
        },
        onSubmit: (values) => {
            console.log(values);
        }
    })
    return (
        <Modal open={isModalOpen} onClose={onCloseHandler}>
            <div className={'bg-white w-[600px] h-[600px] absolute left-[30%] top-[15%] p-4 text-black space-y-4 border-2'}>
                <div>
                    <h1 className={'text-center text-2xl font-bold'}>Get a quote</h1>
                    <h2 className={'text-center'}>Fill out the following information</h2>
                </div>
                <div className={'w-full flex space-x-3'}>
                    <TextField sx={{width:'100%'}}  label={'Name'} variant='standard'/>
                    <TextField sx={{width:'100%'}}  label={'Email'} variant='standard'/>
                    <TextField sx={{width:'100%'}}  label={'Phone'} variant='standard'/>
                </div>
                <div className={'flex w-full space-x-3'}>
                    <LocalizationProvider dateAdapter={AdapterDayjs}>
                        <DatePicker sx={{width:'100%'}} label="Date of event" />
                    </LocalizationProvider>
                    <TextField sx={{width:'100%'}}  label={'Location of event'} variant='standard'/>
                </div>
                <FormControl fullWidth>
                    <InputLabel id="demo-simple-select-label">Age</InputLabel>
                    <Select
                        labelId="demo-simple-select-label"
                        id="demo-simple-select"
                        label="Age"
                    >
                        <MenuItem value={10}>Ten</MenuItem>
                        <MenuItem value={20}>Twenty</MenuItem>
                        <MenuItem value={30}>Thirty</MenuItem>
                    </Select>
                </FormControl>
            </div>
        </Modal>
    );
};

export default QuoteModal;
