import React, {FC, useEffect, useRef, useState} from 'react';
import PackageSelector from '../PackageSelector';
import {equipmentList} from '../../lib/equipment';
const {speaker, bass, booth, movingHead} = equipmentList;
const basicPack = [
    {
        name: speaker.name,
        price: speaker.price,
        quantity: 2
    },
    {
        name: booth.name,
        price: booth.price,
        quantity: 1
    }
];
const standardPack = [
    {
        name: speaker.name,
        price: speaker.price,
        quantity: 2
    },
    {
        name: booth.name,
        price: booth.price,
        quantity: 1
    },
    {
        name: bass.name,
        price: bass.price,
        quantity: 2
    }
];

const deluxePack = [
    {
        name: speaker.name,
        price: speaker.price,
        quantity: 4,
    },
    {
        name: booth.name,
        price: booth.price,
        quantity: 1
    },
    {
        name: bass.name,
        price: bass.price,
        quantity: 2
    },
    {
        name: movingHead.name,
        price: movingHead.price,
        quantity: 2
    },
    ]

const packs = [
    {
        title: 'Basic',
        items: basicPack,
        basePrice: 500
    },
    {
        title: 'Standard',
        items: standardPack,
        basePrice: 600
    },
    {
        title: 'Deluxe',
        items: deluxePack,
        basePrice: 800
    }
    ]
const PackageList:FC<{onPackageSelect: any}> = (props) => {
    const [selectedPackage, setSelectedPackage] = useState('Standard');
    const elementRef = useRef(null);
    const [scrollPosition, setScrollPosition] = useState(0);
    const handleScroll = () => {
        // Access the scrollLeft property of the element
        const newScrollPosition = elementRef.current.scrollLeft;
        setScrollPosition(newScrollPosition);
    };
    useEffect(() => {
        const element = elementRef.current;
        element.addEventListener('scroll', handleScroll);

        // Clean up the event listener when the component unmounts
        return () => {
            element.removeEventListener('scroll', handleScroll);
        };
    }, []);
    const onPackageSelect = (packageType: string) => {
        props.onPackageSelect(packageType);
        setSelectedPackage(packageType);
    }
    const screenWidth = window.innerWidth;
    console.log(screenWidth);
    const desktopStyle = 'flex space-x-0.5 overflow-x-scroll h-full items-center justify-center'

    return (
        <div ref={elementRef} className={`flex space-x-0.5 overflow-x-scroll h-full items-center pl-2 pr-2`}>
            {packs.map((pack) => {
                return (
                        <PackageSelector  onClick={onPackageSelect} title={pack.title} items={pack.items} basePrice={pack.basePrice} isSelected={selectedPackage == pack.title ? true : false}/>
                )
            }   )}
        </div>
    );
};

export default PackageList;
