import React, {FC, useState} from 'react';
import PackageSelector from '../PackageSelector';
import {equipmentList} from '../../lib/equipment';
const {speaker, bass, booth, movingHead} = equipmentList;
const basicPack = [
    {
        name: speaker.name,
        price: speaker.price,
        quantity: 2
    },
    {
        name: booth.name,
        price: booth.price,
        quantity: 1
    }
];
const PackageList:FC<{onPackageSelect: any}> = (props) => {
    return (
        <div className="flex p-8 space-x-5 h-fit overflow-x-scroll">
            <PackageSelector onClick={props.onPackageSelect}  title={'Basic'} items={basicPack} basePrice={500} isSelected={false}/>
            <PackageSelector onClick={props.onPackageSelect}  title={'Standard'} items={basicPack} basePrice={600} isSelected={false}/>
            <PackageSelector onClick={props.onPackageSelect}   title={'Deluxe'} basePrice={800} items={basicPack} isSelected={false}/>
        </div>
    );
};

export default PackageList;
