import React from 'react';
import {Paper} from '@mui/material';
import {useForm} from '../../../Contexts/FormContext';

const FormSummary = () => {
    const {formik} = useForm();
    const values = formik.values;
    const readableDate = formik.values.date && formik.values.date['$d'].toLocaleDateString()
    const readableTime = formik.values.date && formik.values.date['$d'].toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})
    return (
        <Paper>
            <h1 className={'w-full text-center p-3'}>Summary</h1>
            <ul className={'space-y-2'}>
                <h2>Personal Details</h2>
                <li>Name: {values.name}</li>
                <li>Email: {values.email}</li>
                <li>Phone: {values.phone}</li>
                <h2>Event Details</h2>
                <li>Date: {readableDate} at {readableTime}</li>
                <li>{values.type}</li>
                <li>{values.location}</li>
            </ul>
        </Paper>
    );
};

export default FormSummary;
