import React, {FC, useState} from 'react';
import PackageSelector from '../PackageSelector';
import {equipmentList} from '../../lib/equipment';
const {speaker, bass, booth, movingHead} = equipmentList;
const basicPack = [
    {
        name: speaker.name,
        price: speaker.price,
        quantity: 2
    },
    {
        name: booth.name,
        price: booth.price,
        quantity: 1
    }
];
const standardPack = [
    {
        name: speaker.name,
        price: speaker.price,
        quantity: 2
    },
    {
        name: booth.name,
        price: booth.price,
        quantity: 1
    },
    {
        name: bass.name,
        price: bass.price,
        quantity: 2
    }
];

const deluxePack = [
    {
        name: speaker.name,
        price: speaker.price,
        quantity: 2,
    },
    {
        name: booth.name,
        price: booth.price,
        quantity: 1
    },
    {
        name: bass.name,
        price: bass.price,
        quantity: 2
    },
    {
        name: movingHead.name,
        price: movingHead.price,
        quantity: 2
    },
    ]

const packs = [
    {
        title: 'Basic',
        items: basicPack,
        basePrice: 500
    },
    {
        title: 'Standard',
        items: standardPack,
        basePrice: 600
    },
    {
        title: 'Deluxe',
        items: deluxePack,
        basePrice: 800
    }
    ]
const PackageList:FC<{onPackageSelect: (arg:string)=>void}> = (props) => {

    return (
        <div className="flex pl-4 space-x-0.5 overflow-x-scroll ">
            {packs.map((pack) => {
                return (
                    <PackageSelector onClick={props.onPackageSelect} title={pack.title} items={pack.items} basePrice={pack.basePrice}/>
                )
            }   )}
        </div>
    );
};

export default PackageList;
