import React from 'react';
import {Box, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow} from '@mui/material';
import {useForm} from '../../../Contexts/FormContext';
import {usePackage} from '../../../Contexts/PackageContext';
import {packs} from '../../../lib/packages';
import {extraEquipment} from '../../../lib/extraEquipment';
import {eventTypes} from '../../../lib/eventTypes';
import SummarySection from './SummarySection';



const FormSummary = () => {
    const {formik} = useForm();
    const {selectedPackage} = usePackage();
    const values = formik.values;

    const readableDate = formik.values.date && formik.values.date['$d'].toLocaleDateString()
    const readableTime = formik.values.date && formik.values.date['$d'].toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})
    const mapSelectedPackageToPack = (selectedPackage:number) => {
        switch(selectedPackage) {
            case 0:
                return packs[0];
            case 1:
                return packs[1];
            case 2:
                return packs[2];
            case 3:
                return packs[3];
            default:
                return packs[0];
        }
    }
    const mapSelectedTypeToType = (selectedType: number) => {
        const eventType = eventTypes.find((eventType) => eventType.id === selectedType);

        if (eventType) {
            return eventType.name;
        } else {
            return 'Other';
        }
    };
    const personalInfo = [
        {
            title: 'Name',
            value: values.name
        },
        {
            title: 'Email',
            value: values.email
        },
        {
            title: 'Phone',
            value: values.phone
        }
    ]
    const eventInfo = [
        {
            title: 'Date',
            value: values.date && values.date['$d'].toLocaleDateString()
        },
        {
            title: 'Time',
            value: values.date && values.date['$d'].toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})
        },
        {
            title: 'Type',
            value: mapSelectedTypeToType(values.type)
        },
        {
            title: 'Location',
            value: values.location
        },
        {
            title: 'Number of guests',
            value: `${values.guestRange[0]} - ${values.guestRange[1]}`
        }]
    return (
        <Paper className={'p-4 mt-3 max-h-[475px] max-sm:max-h-[525px] overflow-y-scroll'} elevation={4}>
            <h1 className={'w-full text-center'}>Summary</h1>
            <div className={'space-y-4 w-full'}>
                    <SummarySection values={personalInfo} sectionName={'Personal Info'}/>
                    <SummarySection values={eventInfo} sectionName={'Event Info'}/>
                <Box>
                    <h2 className={'text-center font-bold'}>Equipment</h2>
                    <TableContainer component={Box}>
                        <Table aria-label="simple table">
                            <TableHead>
                                <TableRow>
                                        <TableCell>Item</TableCell>
                                        <TableCell align="right">Quantity</TableCell>
                                        {/*<TableCell align="right">Price</TableCell>*/}
                                        {/*<TableCell align="right">Total</TableCell>*/}
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {mapSelectedPackageToPack(selectedPackage).items.map((item) => (
                                    <TableRow
                                        key={item.name}
                                    >
                                        <TableCell component="th" scope="row">
                                            {item.name}
                                        </TableCell>
                                        <TableCell align="right">{item.quantity}</TableCell>
                                    </TableRow>
                                ))}
                                <TableHead>Extra Equipment</TableHead>
                                {values.extraEquipment.map((item:any) => (
                                    <TableRow
                                        key={item.name}
                                        sx={{ '&:last-child td, &:last-child th': { border: 0 } }}
                                            >
                                        <TableCell component="th" scope="row">
                                            {item.name}
                                        </TableCell>
                                        <TableCell align="right">{item.quantity}</TableCell>
                                        <TableCell align="right">${item.price}</TableCell>
                                        <TableCell align="right">${item.price * item.quantity}</TableCell>
                                    </TableRow>
                                ))
                                }
                                {values.extraEquipment.length == 0 && <TableRow>
                                    <TableCell colSpan={4} align={'center'}>No extra equipment</TableCell>
                                </TableRow>}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </Box>

            </div>

        </Paper>
    );
};

export default FormSummary;
