import React, {FC} from 'react';
import PackageSelector from '../PackageSelector';
import {equipmentList} from '../../lib/equipment';
const {speaker, bass, booth, movingHead} = equipmentList;
const basicPack = [
    {
        name: speaker.name,
        price: speaker.price,
        quantity: 2
    },
    {
        name: booth.name,
        price: booth.price,
        quantity: 1
    }
];
const PackageList:FC<{selectedPack?: number}> = (props) => {
    const [selectedPack, setSelectedPack] = React.useState(0);
    console.log(selectedPack)
    const onPackageSelect = (packageType: number) => {
        setSelectedPack(packageType);
    }
    return (
        <div className="flex p-8 space-x-5 h-fit overflow-x-scroll h-1/2">
            <PackageSelector onClick={onPackageSelect(0)}  title={'Basic'} items={basicPack} basePrice={500} isSelected={false}/>
            <PackageSelector onClick={onPackageSelect(1)} title={'Standard'} items={basicPack} basePrice={600} isSelected={false}/>
            <PackageSelector onClick={onPackageSelect(2)}  title={'Deluxe'} basePrice={800} items={basicPack} isSelected={false}/>
        </div>
    );
};

export default PackageList;
