import React from 'react';
import {Box, Paper, Table, TableBody, TableCell, TableContainer, TableFooter, TableHead, TableRow} from '@mui/material';
import {useForm} from '../../../Contexts/FormContext';
import {usePackage} from '../../../Contexts/PackageContext';
import {packs} from '../../../lib/packages';
import {extraEquipment} from '../../../lib/extraEquipment';
import {eventTypes} from '../../../lib/eventTypes';
import SummarySection from './SummarySection';



const FormSummary = () => {
    const {formik} = useForm();
    const {selectedPackage} = usePackage();
    const values = formik.values;
    const mapSelectedPackageToPack = (selectedPackage:number) => {
        switch(selectedPackage) {
            case 0:
                return packs[0];
            case 1:
                return packs[1];
            case 2:
                return packs[2];
            case 3:
                return packs[3];
            default:
                return packs[0];
        }
    }
    const mapSelectedTypeToType = (selectedType: number) => {
        const eventType = eventTypes.find((eventType) => eventType.id === selectedType);

        if (eventType) {
            return eventType.name;
        } else {
            return 'Other';
        }
    };
    const mapSelectedExtraEquipmentToEquipment = (selectedExtraEquipment: number) => {
        const equipment = extraEquipment.find((equipment) => selectedExtraEquipment === equipment.id);
        if(equipment) {
            return equipment;
        } else {
            return {name: 'Other', price: 0}
        }
    }

    const totalExtraEquipmentCost = () => {
        let total = 0;
        values.extraEquipment.forEach((item: any) => {
            total += item.quantity * mapSelectedExtraEquipmentToEquipment(item.id).price;
        });
        return total;
    }

    const personalInfo = [
        {
            title: 'Name',
            value: values.name
        },
        {
            title: 'Email',
            value: values.email
        },
        {
            title: 'Phone',
            value: values.phone
        }
    ]
    const eventInfo = [
        {
            title: 'Date',
            value: values.date && values.date['$d'].toLocaleDateString()
        },
        {
            title: 'Time',
            value: values.date && values.date['$d'].toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})
        },
        {
            title: 'Type',
            value: mapSelectedTypeToType(values.type)
        },
        {
            title: 'Location',
            value: values.location
        },
        {
            title: 'Number of guests',
            value: `${values.guestRange[0]} - ${values.guestRange[1]}`
        }]
    return (
        <Paper className={'p-4 mt-3 max-h-[475px] max-sm:max-h-[525px] overflow-y-scroll overflow-x-hidden'} elevation={4}>
            <h1 className={'w-full text-center'}>Summary</h1>
            <div className={'space-y-4 w-full mt-4'}>
                    <SummarySection values={personalInfo} sectionName={'Personal Info'}/>
                    <SummarySection values={eventInfo} sectionName={'Event Info'}/>
                <h2 className={'text-center font-bold'}>Equipment</h2>
                <Box>
                    <TableContainer component={Box}>
                        <h3><span className={'font-bold'}>Package :</span> {mapSelectedPackageToPack(selectedPackage).title}</h3>
                        <Table aria-label="simple table">
                            <TableHead>
                                <TableRow sx={{fontWeight:'bold'}} >
                                        <TableCell sx={{fontWeight:'bold'}}>Item</TableCell>
                                        <TableCell align="right">Quantity</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {mapSelectedPackageToPack(selectedPackage).items.map((item:any) => (
                                    <TableRow
                                        key={item.name}
                                    >
                                        <TableCell component="th" scope="row">
                                            {item.name}
                                        </TableCell>
                                        <TableCell align="right">{item.quantity}</TableCell>
                                    </TableRow>
                                ))}
                                <TableRow>
                                    <TableCell colSpan={1}  align={'right'}  sx={{fontWeight:'bold'}}>Estimated package price</TableCell>
                                    <TableCell align={'right'} sx={{fontWeight:'bold'}}>${mapSelectedPackageToPack(selectedPackage).price}</TableCell>
                                </TableRow>

                            </TableBody>
                        </Table>
                    </TableContainer>
                    <TableContainer  className={'mt-4'} component={Box}>
                        <h3 className={'font-bold'}>Extra Equipment</h3>
                        <Table>
                            <TableHead>
                                <TableRow>
                                    <TableCell>Item</TableCell>
                                    <TableCell>Quantity</TableCell>
                                    <TableCell>Price</TableCell>
                                    <TableCell align={'left'}>Total</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {values.extraEquipment.map((item : any) => (
                                    <>
                                        {item.quantity > 0 &&
                                            <TableRow
                                                key={item.id}
                                            >
                                                <TableCell component="th" scope="row">
                                                    {item.name}
                                                </TableCell>
                                                <TableCell>{item.quantity}</TableCell>
                                                <TableCell>{mapSelectedExtraEquipmentToEquipment(item.id).price}</TableCell>
                                                <TableCell>{mapSelectedExtraEquipmentToEquipment(item.id).price * item.quantity}</TableCell>
                                            </TableRow>
                                        }
                                    </>
                                ))}
                            </TableBody>
                        </Table>
                    </TableContainer>
                    <TableContainer>
                        <Table>
                            <TableFooter>
                                <TableRow>
                                    <TableCell colSpan={3} align={'right'}><h2>Estimated Extra Equipment Cost</h2></TableCell>
                                    <TableCell align={'right'} sx={{fontWeight:'bold'}}>${totalExtraEquipmentCost()}</TableCell>
                                </TableRow>
                            </TableFooter>
                        </Table>
                    </TableContainer>
                </Box>

            </div>

        </Paper>
    );
};

export default FormSummary;
