import React from 'react';
import {Modal, TextField} from '@mui/material';
import {usePackage} from '../../Contexts/PackageContext';
import {useFormik} from 'formik';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';
import {AdapterDayjs} from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';

const QuoteModal = () => {
    const {isModalOpen, openModalHandler, selectedPackage} = usePackage();
    const onCloseHandler = () => {
        openModalHandler(false);
    }

    const formik = useFormik({
        initialValues: {
            name: '',
            email: '',
            phone: '',
            eventDate: '',
            eventLocation: '',
            eventTime: '',
            eventDuration: '',
            eventDetails: '',
            package: selectedPackage
        },
        onSubmit: (values) => {
            console.log(values);
        }
    })
    return (
        <Modal open={isModalOpen} onClose={onCloseHandler}>
            <div className={'bg-white w-[600px] h-[600px] absolute left-[30%] top-[15%] p-4 text-black border-[] border-2'}>
                <div>
                    <h1 className={'text-center text-2xl font-bold'}>Get a quote</h1>
                    <h2 className={'text-center'}>Fill out the following information</h2>
                </div>
                <div className={'w-full flex space-y-3 space-x-3'}>
                    <TextField  label={'Name'} variant='standard'/>
                    <TextField sx={{width:'300px'}}  label={'Email'} variant='standard'/>
                    <TextField sx={{width:'300px'}}  label={'Phone'} variant='standard'/>
                </div>
                    <LocalizationProvider dateAdapter={AdapterDayjs}>
                        <DatePicker label="Basic date picker" />
                    </LocalizationProvider>
            </div>
        </Modal>
    );
};

export default QuoteModal;
