import {useState, useRef} from 'react';
import './App.css';
import EquipmentComponent from './Components/Equipment/EquipmentComponent';
import PackageSelector from './Components/PackageSelector';
import {equipmentList} from './lib/equipment';

const {speaker, bass, booth, movingHead} = equipmentList;
const basicPack = [
    {
        name: speaker.name,
        price: speaker.price,
        quantity: 2
    },
    {
        name: booth.name,
        price: booth.price,
        quantity: 1
    }
];



function App() {
    const [selectedPackage, setSelectedPackage] = useState('');
    const onPackageSelect = (packageType: string) => {
        setSelectedPackage(packageType);
    }
  return (
      <div className={'background'}>
          <header className='w-full h-[80px] bg-black'>
              test
          </header>
          <div className="">
              <div className="w-full h-[500px] flex justify-center items-center space-x-7 p-5">
                  <EquipmentComponent component='Speaker'/>
                  <EquipmentComponent component='MovingHead'/>
                  <EquipmentComponent component='SpeakerWithBass'/>
                  <EquipmentComponent component='Booth'/>
                  <EquipmentComponent component='SpeakerWithBass'/>
                  <EquipmentComponent component='MovingHead'/>
                  <EquipmentComponent component='Speaker'/>
              </div>
              <div className="flex p-8 space-x-5 h-fit overflow-x-scroll h-1/2">
                  <PackageSelector  title={'Basic'} items={basicPack} basePrice={500}/>
                    <PackageSelector title={'Standard'} items={basicPack} basePrice={600}/>
                    <PackageSelector  title={'Deluxe'} basePrice={800} items={basicPack}/>
              </div>
          </div>

      </div>

  );
}

export default App;
