import {FC, useState} from 'react';
import {Card, Checkbox} from '@mui/material';

const ExtraEquipmentCard:FC<{name: string, img: any}> = (props) => {
    const [selected, setSelected] = useState(false)
    const handleClick = () => {
        setSelected(!selected)
    }
    return (
        <Card onClick={handleClick} className={'relative w-fit flex flex-col justify-center items-center'}>
            <div className={'w-full flex'}>
                <Checkbox checked={selected}/>
                <div className={'flex align-middle items-center'}>
                    <h3 className={'flex justify-end items-center pr-4'}>Quantity</h3>
                    <input className={'w-[50px] h-fit outline-pink-400'} type='number'/>
                </div>
            </div>
           <img width={100} height={100}  src={props.img}/>
            <h1>{props.name}</h1>
        </Card>
    );
};

export default ExtraEquipmentCard;
