import {useState} from 'react';
import './App.css';
import EquipmentComponent from './Components/Equipment/EquipmentComponent';
import PackageList from './Components/PackageList';
import {usePackage} from './Contexts/PackageContext';



function App() {
    const {onPackageSelect, selectedPackage} = usePackage();
    const equipmentRender = () => {
        if(selectedPackage === 0) {
            return (
                <>
                    <EquipmentComponent component='Speaker'/>
                    <EquipmentComponent component='Booth'/>
                    <EquipmentComponent component={'Speaker'}/>
                </>
            )
        }
        else if(selectedPackage === 1) {
            return (
                <>
                    <EquipmentComponent component='SpeakerWithBass'/>
                    <EquipmentComponent component='BoothWithLights'/>
                    <EquipmentComponent component={'SpeakerWithBass'}/>
                </>
            )
        }
        else if(selectedPackage === 2) {
            return (
                <>
                    <EquipmentComponent component='Speaker'/>
                    <EquipmentComponent component={'MovingHead'}/>
                    <EquipmentComponent component='SpeakerWithBass'/>
                    <EquipmentComponent component='BoothWithLights'/>
                    <EquipmentComponent component='SpeakerWithBass'/>
                    <EquipmentComponent component={'MovingHead'}/>
                    <EquipmentComponent component='Speaker'/>
                </>
            )
        }
    }

  return (
      <div className={'background'}>
         <div className={'h-full backdrop-blur-2xl overflow-scroll w-full justify-center'}>
              <div className={'w-full bg-white h-[10%] flex text-center justify-center items-center text-black text-2xl'}>
                  DJ REUVEN EVENTS
              </div>
              <div className="h-[40%] w-full shadow-md flex justify-center bg-white">

                  <div className="w-4/5  h-full flex justify-center items-center mx-sm:px-3 max-sm:space-x-3">
                      {equipmentRender()}
                  </div>
              </div>
              <div className={'h-1/2 w-full p-10'}>
                      <PackageList onPackageSelect={onPackageSelect}/>
              </div>
          </div>
      </div>

  );
}

export default App;
